var validator = require('validator');
module.exports = define;



function define(method,msg,option){
	return new dbvalidator(method,msg,option);
}

function dbvalidator(method,msg,option){
	




	if(validator[method]){
		var msg = msg || method+' error';
		var option = option || null;

		if(typeof option == 'object'){
			return  {validator:use_validator, msg: msg}
			
		}
	}


	function use_validator (val){
		/*创建新数组*/
		var args = option.slice(0);
		args.unshift(val);
		var result = validator[method].apply(null,args);
		return result;
	}

} 


